#!/bin/bash

function tuttest_exec () {
  echo " ==================================================================== " 1>&2
  echo " CMD : tuttest $@                                                     " 1>&2
  echo " OUTPUT:                                                              " 1>&2
  echo ""                                                                       1>&2
  echo "$(tuttest $@)"                                                          1>&2
  echo " -------------------------------------------------------------------- " 1>&2
  echo ""                                                                       1>&2

  tuttest "$@"
}
